    global.set("finished", false);
    global.set("currentImage", camData);
    global.set("lastPacket", camData);
} else if(camData === "KFM"){
    global.set("finished", true);
} else {
    var lastPac = global.get("lastPacket");
    if(lastPac !== camData){
        var partialImage = global.get("currentImage");
        partialImage = partialImage + camData;
        global.set("currentImage", partialImage);
    }
}
msg.payload = global.get("currentImage");
return msg;

var isFullImage = global.get("finished");
if(isFullImage){
    var buf = Buffer.from(msg.payload, 'hex');
    msg.payload = buf;
    global.set("finished", false);
    return msg;
}

